
function [c,ceq] = nonlcon(x)

%   parameters;
    a1 = 0.2;
    a2 = 0.3;
    a3 = 0.1;
    b1 = 1;     %0.3030; %1;
    b2 = 1;     % b2^-1 = 1;
    alpha = 0.3;
    c1 = 1;     %0.08
    c2 = 0.5;   %1667;  %0.5 %39/80;
    c3 = 1;     %81/80;
    c4 = 1;     %0.3;   % 1
    d1 = 0.2;   %0.066;  %0.2
    d2 = 1;
    r1 = 1.5;   %c2+c3
    r2 = 1;
    s = 0.33;   %0.33 %0.05;
    ro = 0.01;  %1;       

    % N  T  I  v
    % x1 x2 x3 x4
   
    % lower bound
%     l = [0.75; 0; 0; 0;];
    % upper bound
%     u = [inf; inf; inf; 1;];
    
    % linear Inequality c.
    A = [];
    b = [];
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                 
    % linear Equality c.
    Aeq = []; 
    beq = [];
    
    c = [-x(1)+0.75; % <= 0
         -x(2);
         -x(3);
         -x(4) ];
    
    v = 1;
     
    ceq = [ -x(1) -x(4)*(x(1)*a3 - x(1)*r2 + x(1)*x(2)*c4 + x(1)^2*b2*r2) -x(1)*a3*exp(-x(4)); 
%             x(2) == - x(4)*(x(2)*a2 - x(2)*r1 + x(3)*x(2)*c2 + x(1)*x(2)*c3 + x(2)^2*b1*r1); % - x(2)*a2*exp(-x(4));
            -x(3) -x(3)*a1*exp(-x(4)) -(x(4)*(x(3)*x(2)*a1 - alpha*s - x(2)*s + x(3)*x(2)*d1 + x(3)*a1*alpha + x(3)*alpha*d1 - x(3)*x(2)*ro + x(3)*x(2)^2*c1 + x(3)*x(2)*alpha*c1))/(x(2) + alpha);
            -x(4) +(v*(v - 2*d2*x(4)))/2  ];
%             v == 0  ];
   
    
end